import React, { useState } from "react";
import axios from "axios";
import {useNavigate} from  'react-router-dom';

const Login = () => {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const navigate = useNavigate();
    const [lemail, setLemail] = useState('');
    const [lpassword, setLpassword] = useState('');
    const [lmessage, setLmessage] = useState('');

    const handleSubmitl = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('/login', {
                lemail,
                lpassword
            });
            setLmessage(response.data);
            sessionStorage.setItem("user",lemail);
            navigate(`/`);
        } catch (error) {
            console.error('Error logging in:', error);
            setLmessage('Error logging in. Please try again.');
        }
    };


    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('/signup', {
                name,
                email,
                password
            });
            setMessage(response.data);
            sessionStorage.setItem("user",lemail);
            navigate(`/`);
        } catch (error) {
            console.error('Error signing up:', error);
            setMessage('Error signing up. Please try again.');
        }
    };

    return (
        <div>
            <h2>Sign Up</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Name"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    required
                />
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <button type="submit">Sign Up</button>
            </form>
            {message && <p>{message}</p>}


            <h2>Login</h2>
            <form onSubmit={handleSubmitl}>
                <input
                    type="email"
                    placeholder="Email"
                    value={lemail}
                    onChange={(e) => setLemail(e.target.value)}
                    required
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={lpassword}
                    onChange={(e) => setLpassword(e.target.value)}
                    required
                />
                <button type="submit">Login</button>
            </form>
            {lmessage && <p>{lmessage}</p>}

        </div>
    );
}

export default Login;
