import React,{useState} from "react";


export default function Signup(){
    const [values,setValues] = useState({
        name:"",
        email:"",
        password:""
    })
    const [error,setError] = useState([])
    const handleInput = (event)=>{
        setValues(prev=>({...prev,[event.target.name]:[event.target.value]}))
    }
    const handlesubmit = (event)=>{
        event.preventDefault();
        console.log(values);
        setError(Validation(values));
    }
    function Validation(values){
        let error = {}
        const email_pattern=/^[^\s@]+@[^\s@]+\.[^\s@]+$/
        const password_pattern = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])[a-zA-Z0-9]{8,}$/
    
        if(values.name ===""){
            error.name = 'Name should not be empty'
        }
        else{
            error.email = ""
        }
        if(values.email ===""){
            error.email = 'Email should not be empty'
        }
        else if (!email_pattern.test(values.email)){
            error.email ="Email Didn't match"
        }
        else{
            error.email = ""
        }
        if (values.password===""){
            error.password="Password should not be empty"
        }
        else if(!password_pattern.test(values.password)){
            error.password = "Password didn't match"
        }else{
            error.password = ""
        }
        return error;
    }
    
    return(
        <div className='d-flex justify-content-center alighn-items-center bg-primary vh-100'>
            <div className='bg-white p-3 rounded w-25'>
                <form action="" onSubmit={handlesubmit}>
                    <div className='mb-3'>
                        <label htmlFor='name'>Name</label>
                        <input type='text' placeholder='enter Name' onChange={handleInput}/>
                        <span>{error.name && <span className='text-danger'>{error.name}</span>}</span>
                    </div>
                    <div className='mb-3'>
                        <label htmlFor='email'>Email</label>
                        <input type='email' placeholder='enter email' onChange={handleInput}/>
                        <span>{error.email && <span className='text-danger'>{error.email}</span>}</span>
                    </div>
                    <div className='mb-3'>
                        <label htmlFor='password'>Password</label>
                        <input type='password' placeholder='enter password' onChange={handleInput}/>
                        <span>{error.password && <span className='text-danger'>{error.password}</span>}</span>
                    </div>
                    <button type='submit' className='btn'>Sign Up</button>
                    <p>you are agree to terms and condition</p>
                </form>
            </div>
        </div>
    )
}
